mapper.select:

SELECT CASE WHEN EXISTS (
            SELECT 1
            FROM USER_INFO
            WHERE LOGIN_ID = #{LOGIN_ID}
            ) THEN 'Y' ELSE 'N' END USER_YN
FROM DUAL


SELECT CASE WHEN EXISTS (
            SELECT 1
            FROM USER_INFO
            WHERE CI = #{KAKAO_CUST_ID}
            ) THEN 'Y' ELSE 'N' END USER_YN
FROM DUAL
